import { useEffect, useState } from "react";
import Container from "react-bootstrap/esm/Container";
import Navbaar from "./components/Navbaar";
import axios from 'axios';
import Movieslist from "./components/Movieslist";
import {BrowserRouter,Routes,Route  } from "react-router-dom";
function App() {

  const[moviess,setMoviess]=useState([]);
  const[totalp,settotalp]=useState([0]);

  const  getAllData=async()=>
  {
const res=await axios.get("https://api.themoviedb.org/3/movie/popular?api_key=872b6f6702d22707bd5586523dc92a11&language=ar-US")
setMoviess(res.data.results)
settotalp(res.data.total_pages);
  }
  const  getpage=async(page)=>
  {
const res=await axios.get(`https://api.themoviedb.org/3/movie/popular?api_key=872b6f6702d22707bd5586523dc92a11&language=ar-US&page=${page}`)
setMoviess(res.data.results)
  settotalp(res.data.total_pages);

  }
  useEffect(()=>{
    getAllData();
  },[])

  const search=async(word)=>
  {
    if(word==="")
    {
      getAllData();

    }
    else
    {
      const res=await axios.get(`https://api.themoviedb.org/3/search/movie?api_key=872b6f6702d22707bd5586523dc92a11&query=${word}&language=ar&page=1&include_adult=false`);
      setMoviess(res.data.results)
      settotalp(res.data.total_pages);

    }
  }
  return (

    <div className="body">

      <Navbaar search={search}/>
      <BrowserRouter>
        <Routes>
          <Route>
          <Container className="px-2">
      <Movieslist movies={moviess} getpage={getpage} totalp={totalp}/> 

      </Container>
          </Route>
        </Routes>
      </BrowserRouter>

    </div>
  );
}

export default App;
