import React from 'react'
import Button from 'react-bootstrap/Button';
import Container from 'react-bootstrap/Container';
import Col from 'react-bootstrap/esm/Col';
import Form from 'react-bootstrap/Form';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import NavDropdown from 'react-bootstrap/NavDropdown';
import Row from 'react-bootstrap';
import {getallmovies}from '../redux/action/Moviesaction'
import  logo from "../images/logo512.png";
import { useDispatch } from 'react-redux';
const Navbaar = () => {
  const dispatch=useDispatch();
  const search=async(word)=>
  {
    if(word==="")
    {
    dispatch( getallmovies());

    }
    else
    {
      const res=await axios.get(`https://api.themoviedb.org/3/search/movie?api_key=872b6f6702d22707bd5586523dc92a11&query=${word}&language=ar&page=1&include_adult=false`);
       setMoviess(res.data.results)
       settotalp(res.data.total_pages);

    }
  }
  const onsearch=(word)=>
  {
search(word);
  }
  return (
    
 
 <Navbar bg="" expand="lg" style={{background:'rgb(231, 41, 41)'}}>
    <Container >
    

    
      <Navbar.Brand href="/"><img className='logo' src={logo}/></Navbar.Brand>
      <Navbar.Toggle aria-controls="navbarScroll" />
      <Navbar.Collapse id="navbarScroll">
           
       
   
    
          
        <Form className="d-flex w-100">
          <Form.Control
            type="text"
            placeholder="Search "
            className="me-2 se"
            aria-label="Search"
            onChange={(e)=>onsearch(e.target.value)}
          />
          
        </Form>
      </Navbar.Collapse>
    </Container>
  </Navbar>
  

  )
}

export default Navbaar
