import { getpage } from '../redux/action/Moviesaction';
const Paginationcomponent = () => {


  const[pagecount,setpagecount]=useState(0);
  const dispatch=useDispatch();


 const pages=useSelector(state=>state.pageCount)
 useEffect(()=>{
  setpagecount(pages);
 },[])
   

  

